import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchDepartments } from '../redux/slices/department';
import { Grid, Card, CardContent, Typography, CardMedia } from '@mui/material';
import { Link } from 'react-router-dom';

const HomePage = () => {
    const dispatch = useDispatch();
    const departments = useSelector(state => state.departments.departments.slice(0, 4)); // Изменил переменную на departments
    const parts = useSelector(state => state.parts.parts.slice(0, 4));
    const services = useSelector(state => state.services.services.slice(0, 4));

    useEffect(() => {
        dispatch(fetchDepartments()); // Использую fetchDepartments
    }, [dispatch]);

    const renderDepartments = () => (
        <Grid container spacing={2}>
            {departments.map(department => (
                <Grid item key={department._id} xs={12} sm={6} md={4}>
                    <Card>
                        <CardContent>
                            <Typography variant="h5">{department.name}</Typography>
                            {/* Добавьте другие поля отдела, которые хотите отобразить */}
                        </CardContent>
                    </Card>
                </Grid>
            ))}
        </Grid>
    );

    // Ваши функции renderParts и renderServices оставьте без изменений

    return (
        <div>
            {/* Остальная часть вашего компонента без изменений */}
            <Typography variant="h4" gutterBottom>
                Добро пожаловать в наш автосервис
            </Typography>
            <Typography variant="body1" style={{ marginBottom: '2rem' }}>
                {/* Ваш текст */}
            </Typography>

            <Typography variant="h4" gutterBottom>
                Мы оказываем услуги
            </Typography>
            {renderServices()}
            <Typography>
                <Link to='/services'>
                    подробнее
                </Link>
            </Typography>

            <Typography variant="h4" gutterBottom style={{ marginTop: '2rem' }}>
                Мы являемся официальным дистрибьютором лучших автозапчастей
            </Typography>
            {renderParts()}
            <Typography>
                <Link to='/parts'>
                    подробнее
                </Link>
            </Typography>

            <Typography variant="h4" gutterBottom style={{ marginTop: '2rem' }}>
                Наши мастера
            </Typography>
            {renderDepartments()} {/* Заменил renderEmployees на renderDepartments */}
            <Typography>
                <Link to='/departments'> {/* Заменил на '/departments' */}
                    подробнее
                </Link>
            </Typography>
        </div>
    );
};

export default HomePage;
