import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchEmployees, createEmployee, deleteEmployee, updateEmployee } from '../redux/slices/employees';
import { Grid, Card, CardContent, Typography, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField } from '@mui/material';
import axios from '../redux/axios';

const EmployeesAdminPage = ({ employeeAccess }) => {
    const dispatch = useDispatch();
    const employees = useSelector(state => state.employees.employees);
    const [openEditDialog, setOpenEditDialog] = useState(false);
    const [openCreateDialog, setOpenCreateDialog] = useState(false);
    const [currentEmployee, setCurrentEmployee] = useState(null);
    const [newEmployee, setNewEmployee] = useState({ name: '', granted: false });

    useEffect(() => {
        dispatch(fetchEmployees());
    }, [dispatch]);

    const handleOpenEditDialog = (employee) => {
        setCurrentEmployee(employee);
        setOpenEditDialog(true);
    };

    const handleDelete = (id) => {
        dispatch(deleteEmployee(id));
    };

    const handleCloseEditDialog = () => {
        setOpenEditDialog(false);
        setCurrentEmployee(null);
    };

    const handleOpenCreateDialog = () => {
        setOpenCreateDialog(true);
    };

    const handleCloseCreateDialog = () => {
        setOpenCreateDialog(false);
        setNewEmployee({ name: '', granted: false });
    };

    const handleChange = (e, isCurrentEmployee = true) => {
        const employee = isCurrentEmployee ? currentEmployee : newEmployee;
        const updatedEmployee = { ...employee, [e.target.name]: e.target.value };
        if (isCurrentEmployee) {
            setCurrentEmployee(updatedEmployee);
        } else {
            setNewEmployee(updatedEmployee);
        }
    };

    const handleCreateOrUpdateEmployee = async (isCreate) => {
        const employeeData = isCreate ? newEmployee : currentEmployee;

        if (isCreate) {
            dispatch(createEmployee(employeeData));
            handleCloseCreateDialog();
        } else {
            dispatch(updateEmployee({ id: currentEmployee._id, updatedData: employeeData }));
            handleCloseEditDialog();
        }
    };

    return (
        <div>
            <Typography variant="h4" gutterBottom>Управление сотрудниками</Typography>
            {employeeAccess.canCreate && <Button color="primary" onClick={handleOpenCreateDialog}>Добавить сотрудника</Button>}
            <Grid container spacing={3}>
                {employees.map(employee => (
                    <Grid item key={employee._id} xs={12} sm={6} md={4}>
                        <Card>
                            <CardContent>
                                <Typography variant="h5">{employee.name}</Typography>
                                <Typography variant="body2">Доступ: {employee.granted ? 'разрешен' : 'запрещен'}</Typography>
                                {employeeAccess.canEdit && <Button color="primary" onClick={() => handleOpenEditDialog(employee)}>Изменить</Button>}
                                {employeeAccess.canDelete && <Button color="secondary" onClick={() => handleDelete(employee._id)}>Удалить</Button>}
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            {/* Dialog for Creating a New Employee */}
            <Dialog open={openCreateDialog} onClose={handleCloseCreateDialog}>
                <DialogTitle>Добавить нового сотрудника</DialogTitle>
                <DialogContent>
                    <TextField
                        autoFocus
                        margin="dense"
                        name="name"
                        label="Имя"
                        type="text"
                        fullWidth
                        variant="standard"
                        value={newEmployee.name}
                        onChange={(e) => handleChange(e, false)}
                    />
                    <TextField
                        margin="dense"
                        name="granted"
                        label="Доступ (true/false)"
                        type="text"
                        fullWidth
                        variant="standard"
                        value={newEmployee.granted}
                        onChange={(e) => handleChange(e, false)}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseCreateDialog}>Отмена</Button>
                    <Button onClick={() => handleCreateOrUpdateEmployee(true)}>Создать</Button>
                </DialogActions>
            </Dialog>

            {/* Dialog for Editing an Employee */}
            <Dialog open={openEditDialog} onClose={handleCloseEditDialog}>
                <DialogTitle>Изменить сотрудника</DialogTitle>
                <DialogContent>
                    <TextField
                        autoFocus
                        margin="dense"
                        name="name"
                        label="Имя"
                        type="text"
                        fullWidth
                        variant="standard"
                        value={currentEmployee?.name}
                        onChange={(e) => handleChange(e, true)}
                    />
                    <TextField
                        margin="dense"
                        name="granted"
                        label="Доступ (true/false)"
                        type="text"
                        fullWidth
                        variant="standard"
                        value={currentEmployee?.granted}
                        onChange={(e) => handleChange(e, true)}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseEditDialog}>Отмена</Button>
                    <Button onClick={() => handleCreateOrUpdateEmployee(false)}>Сохранить</Button>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default EmployeesAdminPage;
