import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchHazardousJobs } from '../redux/slices/hazardousJob';
import { Grid, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';

const HazardousJobAdmin = () => {
    const dispatch = useDispatch();
    const hazardousJobs = useSelector(state => state.hazardousJobs.hazardousJobs);
    const status = useSelector(state => state.hazardousJobs.status);
    const error = useSelector(state => state.hazardousJobs.error);

    useEffect(() => {
        dispatch(fetchHazardousJobs());
    }, [dispatch]);

    return (
        <div>
            <Typography variant="h4" gutterBottom>Управление опасными работами</Typography>
            <Grid container spacing={3}>
                {status === 'loading' && <div>Loading...</div>}
                {status === 'failed' && <div>Error: {error}</div>}
                {status === 'succeeded' && (
                    <TableContainer component={Paper} sx={{ maxWidth: '90%' }}>
                        <Table size="small">
                            <TableHead>
                                <TableRow>
                                    <TableCell>ID</TableCell>
                                    <TableCell>Название работы</TableCell>
                                    <TableCell>Описание</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {hazardousJobs.map(hazardousJob => (
                                    <TableRow key={hazardousJob._id}>
                                        <TableCell>{hazardousJob._id}</TableCell>
                                        <TableCell>{hazardousJob.jobTitle}</TableCell>
                                        <TableCell>{hazardousJob.description}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                )}
            </Grid>
        </div>
    );
};

export default HazardousJobAdmin;
