import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { createEmployeeAccess, deleteEmployeeAccess, updateEmployeeAccess } from '../redux/slices/employeeAccess';

const EmployeeAccessManager = () => {
    const dispatch = useDispatch();
    const [formData, setFormData] = useState({
        name: '',
        granted: false,
    });

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleAddRecord = () => {
        dispatch(createEmployeeAccess(formData));
        setFormData({ name: '', granted: false });
    };

    const handleDeleteRecord = (id) => {
        dispatch(deleteEmployeeAccess(id));
    };

    const handleUpdateRecord = (id, updatedData) => {
        dispatch(updateEmployeeAccess({ id, updatedData }));
    };

    return (
        <div>
            <h2>Управление доступом сотрудников</h2>
            <form>
                <label>
                    Имя:
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleFormChange}
                    />
                </label>
                <label>
                    Доступ разрешен:
                    <input
                        type="checkbox"
                        name="granted"
                        checked={formData.granted}
                        onChange={handleFormChange}
                    />
                </label>
                <button type="button" onClick={handleAddRecord}>Добавить</button>
            </form>
            {/* Здесь вы можете добавить функционал для удаления и изменения записей */}
        </div>
    );
};

export default EmployeeAccessManager;
