import React, { useState } from 'react';
import {
    Button,
    Container,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TextField,
    Typography
} from '@mui/material';

function AdminEmployeeAccess() {
    const [formData, setFormData] = useState({
        name: '',
        granted: false,
    });

    const [data, setData] = useState([
        // Здесь вы можете определить начальные данные для таблицы
    ]);

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleAddRecord = () => {
        setData([...data, formData]);
        setFormData({ name: '', granted: false });
    };

    const handleDeleteRecord = (index) => {
        const newData = [...data];
        newData.splice(index, 1);
        setData(newData);
    };

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Управление доступом сотрудников (администратор)
            </Typography>
            {/* Форма для добавления записей */}
            <Paper elevation={3} style={{ padding: '20px', marginBottom: '20px' }}>
                {/* Поля формы для ввода данных */}
                <TextField
                    name="name"
                    label="Имя"
                    value={formData.name}
                    onChange={handleFormChange}
                    fullWidth
                    margin="normal"
                />
                <TextField
                    name="granted"
                    label="Доступ разрешен"
                    type="checkbox"
                    checked={formData.granted}
                    onChange={handleFormChange}
                    fullWidth
                    margin="normal"
                />
                {/* Добавление записи */}
                <Button variant="contained" color="primary" onClick={handleAddRecord}>
                    Добавить запись
                </Button>
            </Paper>
            {/* Таблица для отображения данных */}
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            {/* Заголовки таблицы */}
                            <TableCell>Номер</TableCell>
                            <TableCell>Имя</TableCell>
                            <TableCell>Доступ разрешен</TableCell>
                            <TableCell>Действия</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {/* Отображение данных в таблице */}
                        {data.map((row, index) => (
                            <TableRow key={index}>
                                <TableCell>{index + 1}</TableCell>
                                <TableCell>{row.name}</TableCell>
                                <TableCell>{row.granted ? 'Да' : 'Нет'}</TableCell>
                                {/* Здесь добавьте вывод данных из таблицы */}
                                <TableCell>
                                    <Button variant="contained" color="secondary" onClick={() => handleDeleteRecord(index)}>
                                        Удалить
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Container>
    );
}

export default AdminEmployeeAccess;
