import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Tabs, Tab } from '@mui/material';
import { fetchEmployeeAccess } from '../redux/slices/employeeAccess';

const AdminPage = () => {
    const dispatch = useDispatch();
    const [selectedTab, setSelectedTab] = useState(0);

    const isLoading = useSelector(state => state.employeeAccess.status === 'loading');

    useEffect(() => {
        dispatch(fetchEmployeeAccess());
    }, [dispatch]);

    const handleTabChange = (event, newValue) => {
        setSelectedTab(newValue);
    };

    return (
        <div>
            <Tabs value={selectedTab} onChange={handleTabChange}>
                <Tab label="Сотрудники" />
                {/* Добавьте здесь остальные вкладки */}
            </Tabs>
            {/* Отображаем загрузку, если данные загружаются */}
            {isLoading && <div>Loading...</div>}
            {/* Отображаем содержимое в зависимости от выбранной вкладки */}
            {selectedTab === 0 && <EmployeeSection />}
            {/* Добавьте здесь остальные разделы */}
        </div>
    );
};

const EmployeeSection = () => {
    const employeeAccessData = useSelector(state => state.employeeAccess.employeeAccess);

    return (
        <div>
            {/* Здесь вы можете отобразить данные о доступе сотрудников */}
            {employeeAccessData.map(access => (
                <div key={access.id}>
                    {/* Отобразите информацию о доступе сотрудника */}
                </div>
            ))}
        </div>
    );
};

export default AdminPage;
